import React from 'react';
import { Toaster } from 'react-hot-toast';
import MintTokenForm from './components/MintTokenForm';
import TransferForm from './components/TransferForm';
import WalletView from './components/WalletView';
import TransactionHistory from './components/TransactionHistory';
import { useTokens } from './hooks/useTokens';

function App() {
  const { tokens, wallets, transactions, refreshData } = useTokens();

  return (
    <>
      <header style={{ padding: '1rem 1.5rem', borderBottom: '1px solid #eee' }}>
        <h1 style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', margin: 0 }}>
          <span role="img" aria-label="coin">ðŸª™</span>
          CryptoToken Manager
        </h1>
        <p style={{ margin: '0.25rem 0 0 0' }}>Create and manage your own cryptocurrency tokens</p>
      </header>

      <main style={{ padding: '1.5rem' }}>
        <section style={{ marginBottom: '1.25rem' }}>
          <h2>Created Tokens</h2>
          {tokens && tokens.length > 0 ? (
            <ul>
              {tokens.map((t) => (
                <li key={t.address || t.id}>
                  {t.name} ({t.symbol}) â€” total: {t.totalSupply ?? 'â€”'}
                </li>
              ))}
            </ul>
          ) : (
            <p>No tokens created yet.</p>
          )}
        </section>

        {/* Forms and views */}
        <MintTokenForm refreshData={refreshData} />
        <TransferForm refreshData={refreshData} />

        <section style={{ marginTop: '1.5rem' }}>
          <h2>Wallets</h2>
          <WalletView wallets={wallets} />
        </section>

        <section style={{ marginTop: '1.5rem' }}>
          <h2>Transactions</h2>
          <TransactionHistory transactions={transactions} />
        </section>

        <Toaster />
      </main>
    </>
  );
}

export default App;
